import React from 'react'
import { FixedSizeList as List } from 'react-window'
import './VirtualBookList.css'

// –ü—Ä–∏–º–µ—Ä –º–æ–∫-–¥–∞–Ω–Ω—ã—Ö –Ω–∞ 1000 –∫–Ω–∏–≥ (–≥–µ–Ω–µ—Ä–∏—Ä—É–µ–º –∫–∞–∫ –≤ –∫–Ω–∏–≥–µ)
const books = Array(1000).fill().map((_, index) => ({
  id: index + 1,
  title: `–ö–Ω–∏–≥–∞ ‚Ññ${index + 1}`,
  author: `–ê–≤—Ç–æ—Ä ${index + 1}`,
  price: 500 + (index % 100),
}))

function BookRow({ index, style }) {
  const book = books[index]
  return (
    <div className="book-row" style={style}>
      <h4>{book.title}</h4>
      <p>{book.author}</p>
      <span>{book.price} ‚ÇΩ</span>
    </div>
  )
}

export default function VirtualBookList() {
  return (
    <div className="virtual-list-container">
      <h2>üìö –í–∏—Ä—Ç—É–∞–ª–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã–π —Å–ø–∏—Å–æ–∫ –∫–Ω–∏–≥</h2>
      <List
        height={500}
        itemCount={books.length}
        itemSize={80}
        width="100%"
      >
        {BookRow}
      </List>
    </div>
  )
}
