import React from 'react'
import './Cart.css'
import { useCart } from '../../context/CartContext'

function Cart() {
  const { cartItems, removeFromCart, clearCart } = useCart()

  const totalPrice = cartItems.reduce((sum, book) => sum + book.price, 0)

  return (
    <div className="cart-page">
      <h2>üõí –ö–æ—Ä–∑–∏–Ω–∞</h2>

      {cartItems.length === 0 ? (
        <p>–í–∞—à–∞ –∫–æ—Ä–∑–∏–Ω–∞ –ø—É—Å—Ç–∞</p>
      ) : (
        <>
          <ul className="cart-list">
            {cartItems.map(book => (
              <li key={book.id} className="cart-item">
                <img src={book.cover} alt={book.title} className="cart-cover" />
                <div>
                  <h4>{book.title}</h4>
                  <p>{book.author}</p>
                  <p>{book.price} ‚ÇΩ</p>
                  <button onClick={() => removeFromCart(book.id)}>–£–¥–∞–ª–∏—Ç—å</button>
                </div>
              </li>
            ))}
          </ul>

          <div className="cart-summary">
            <p>–°—É–º–º–∞ –∑–∞–∫–∞–∑–∞: <strong>{totalPrice} ‚ÇΩ</strong></p>
            <button className="clear-btn" onClick={clearCart}>–û—á–∏—Å—Ç–∏—Ç—å –∫–æ—Ä–∑–∏–Ω—É</button>
          </div>
        </>
      )}
    </div>
  )
}

export default Cart
