  const book = data[index];
  return (
    <div className="book-row" style={style}>
      <div className="book-info">
        <h4>{book.title}</h4>
        <p>{book.author}</p>
      </div>
      <span className="book-price">{book.price} ‚Ç∏</span>
    </div>
  );
}

